import React from 'react';

interface QvsLogoProps {
  className?: string;
  alt?: string;
  style?: React.CSSProperties;
}

/**
 * QVS Logo component with SVG fallback
 * This component will try to load the QVS logo from the '/images/logo-qvs.png' path
 * If that fails, it will render a fallback SVG
 */
const QvsLogo: React.FC<QvsLogoProps> = ({ 
  className = 'logo-qvs', 
  alt = 'QVS-Pro Logo',
  style 
}) => {
  return (
    <img 
      src="/images/logo-qvs.png" 
      alt={alt} 
      className={className}
      style={style}
      onError={(e) => {
        const target = e.target as HTMLImageElement;
        // Prevent infinite error loops
        target.onerror = null;
        
        // Use the fallback SVG data
        target.src = 'data:image/svg+xml;base64,';
      }}
    />
  );
};

export default QvsLogo; 